// Generic Helper Function to Replace Resend Logic
export const sendEmail = async ({ to, subject, html, text, attachments, reply_to }) => {
  try {
    const info = await transporter.sendMail({
      from: `"${process.env.RESEND_FROM_NAME || 'Rupie Times'}" <${process.env.RESEND_FROM_EMAIL || process.env.SMTP_USER}>`,
      to,
      subject,
      text: text || "Please enable HTML to view this email.",
      html,
      attachments: attachments || [],
      replyTo: reply_to
    });

    // console.log(" Email sent:", info.messageId);
    return { success: true, data: { id: info.messageId } };
  } catch (error) {
    console.error(" Nodemailer send error:", error);
    return { success: false, error: error.message };
  }
};


export const sendOTPEmail = async (toEmail, otp, name = 'User') => {
  try {
    const result = await sendEmail({
      to: toEmail,
      subject: 'Your Password Reset OTP - Rupie Times',
      html: generateOTPEmailHTML(name, otp),
      text: generateOTPEmailText(name, otp),
      attachments: SOCIAL_ATTACHMENTS
    });

    if (!result.success) {
      throw new Error(result.error);
    }

    return result;
  } catch (error) {
    console.error('Error sending OTP email:', error);
    throw error;
  }
};

