export const sendContactFormEmail = async (formData) => {
  const { fullName, email, number, subject, message } = formData;

  try {
    const content = `
      <h2 style="color: ${BRAND_COLORS.primary}; margin-bottom: 20px;">New Contact Message</h2>
      <p><strong>Name:</strong> ${fullName}</p>
      <p><strong>Email:</strong> ${email}</p>
      <p><strong>Phone:</strong> ${number || 'Not provided'}</p>
      <p><strong>Subject:</strong> ${subject}</p>
      <hr style="border-top: 1px solid #eee; margin: 20px 0;" />
      <h3 style="color: ${BRAND_COLORS.primary};">Message:</h3>
      <p style="white-space: pre-wrap; background: #f9f9f9; padding: 15px; border-radius: 5px;">${message}</p>
    `;

    const result = await sendEmail({
      to: process.env.CONTACT_EMAIL || 'valab1203@gmail.com',
      reply_to: email, // Note camelCase in Nodemailer is replyTo, handled in sendEmail
      subject: `New Contact Form Submission: ${subject}`,
      html: generateEmailTemplate(content),
      attachments: SOCIAL_ATTACHMENTS
    });

    if (!result.success) {
      throw new Error(result.error);
    }

    return result;
  } catch (error) {
    console.error('Error sending contact email:', error);
    throw error;
  }
};

