function generateOTPEmailHTML(name, otp) {
  // Split OTP into digits for box display
  const digits = otp.toString().split('');
  const otpBoxes = digits.map(d => `
    <span style="
      display: inline-block; 
      width: 50px; 
      height: 60px; 
      line-height: 60px; 
      background-color: ${BRAND_COLORS.otpBox}; 
      color: #000; 
      font-size: 28px; 
      font-weight: bold; 
      margin: 0 8px; 
      border-radius: 8px; 
      text-align: center;
      box-shadow: 0 1px 2px rgba(0,0,0,0.05);
    ">${d}</span>
  `).join('');

  const content = `
    <h1 style="font-size: 26px; font-weight: 600; color: #000; margin-bottom: 25px;">Hi ${name},</h1>
    
    <p style="font-size: 16px; line-height: 1.6; color: #4B5563; margin-bottom: 30px;">
      You have requested to reset your password for your Rupie Times account. 
      Please use the following One-Time Password (OTP) to verify your identity
    </p>
    
    <div style="text-align: center; margin: 40px 0;">
      <div>${otpBoxes}</div>
    </div>
    
    <p style="font-size: 15px; color: #4B5563; margin-bottom: 40px;">
      OTP will expire in <strong>10 minutes</strong>.
    </p>
    
    <div style="margin-top: 50px;">
      <p style="margin: 0; font-size: 16px; color: #333;">Best Regards,</p>
      <p style="margin: 5px 0 0 0; font-size: 16px; font-weight: bold; color: #000;">Rupies Times Team</p>
    </div>
  `;

  return generateEmailTemplate(content);
}

