function generateOTPEmailText(name, otp) {
  return `
    Hi ${name},
    
    You have requested to reset your password for your Rupie Times account. Please use the following One-Time Password (OTP) to verify your identity:
    
    ${otp.split('').join(' ')}
    
    OTP will expire in 10 minutes.
    
    Best Regards,
    Rupies Times Team
  `;
}

function generatePasswordResetSuccessText(name) {
  return `
    Hi ${name},
    
    Password Reset Successful
    Your Rupie Times account password has been successfully updated.
    
    If you did not make this change, please contact our support team immediately.
    
    Login to Account: ${process.env.NEXT_PUBLIC_APP_URL || 'https://rupietimes.com'}/auth/login
    
    Best Regards,
    Rupies Times Team
  `;
}

